function cacheError(){alert("Errore nella richiesta.")}function cacheToggled(a){$("#toggle_cache > p > strong").html(a.state);$("#toggle_cache > form > fieldset > input").attr("value",a.command);if(a.purge_enabled){$("#purge_cache").css("display","block")}else{$("#purge_cache").css("display","none")}}function toggleCache(a){$.ajax({error:cacheError,success:cacheToggled,type:"POST",url:a});return false}function cachePurged(a){if(a.success){alert("Cache Purged")}else{cacheError()}}function purgeCache(a){$.ajax({error:cacheError,success:cachePurged,type:"POST",url:a});return false};